CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "invt_core"."composite_items"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "name" text NOT NULL, "unit" text NOT NULL, "tax_id" text NOT NULL, "description" text NOT NULL, "purchase_account_id" uuid NOT NULL, "account_id" uuid NOT NULL, "inventory_account_id" uuid NOT NULL, "status" text NOT NULL, "source" text NOT NULL, "is_combo_product" boolean NOT NULL, "item_type" text NOT NULL, "rate" float8 NOT NULL, "pricebook_rate" float8 NOT NULL, "purchase_rate" float8 NOT NULL, "reorder_level" float8 NOT NULL, "initial_stock" float8 NOT NULL, "initial_stock_rate" float8 NOT NULL, "vendor_id" uuid NOT NULL, "stock_on_hand" float8 NOT NULL, "asset_value" float8 NOT NULL, "available_stock" float8 NOT NULL, "actual_available_stock" float8 NOT NULL, "sku" text NOT NULL, "upc" text NOT NULL, "ean" Text NOT NULL, "isbn" text NOT NULL, "part_number" text NOT NULL, "image_id" text NOT NULL, "image_name" text NOT NULL, "purchase_description" text NOT NULL, PRIMARY KEY ("id") );
