CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "invt_core"."customer_payments"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "payment_mode" text NOT NULL, "amount" float8 NOT NULL, "amount_refunded" float8 NOT NULL, "bank_charges" float8 NOT NULL, "date" date NOT NULL, "status" text NOT NULL, "reference_number" text NOT NULL, "description" text NOT NULL, "customer_id" uuid NOT NULL, "tax_amount_withheld" float8 NOT NULL, "invoices" uuid NOT NULL, "exchange_rate" float8 NOT NULL, "currency_id" uuid NOT NULL, "account_id" uuid NOT NULL, "tax_account_id" uuid NOT NULL, "tax_account_name" text NOT NULL, "unused_amount" float8 NOT NULL, "last_four_digits" integer NOT NULL, PRIMARY KEY ("id") );
